import React from 'react';
import { useParams } from 'react-router-dom';
import cardData from '../data/card_data.json';
import './CardDetail.css';

export default function CardDetail() {
    const { id } = useParams();
    const card = cardData.find((item, index) => index.toString() === id);

    if (!card) return <p>Card not found</p>;

    return (
        <div className="card-detail-container">
            <img src={card.image_url} alt={card.title} className="card-detail-image" />
            <div className="card-detail-content">
                <h2>{card.title}</h2>
                <p className="card-detail-rating">Rating: ‚≠ê {card.rating} ({card.reviews} reviews)</p>
                <p className="card-detail-time">Duration: {card.time}</p>
                <p className="card-detail-price">Price: {card.price}</p>
                <p className="card-detail-description">Here you can add more details or description about the experience.</p>
            </div>
        </div>
    );
}
