import { useRef, useEffect } from 'react';
import cardData from '../data/card_data.json';
import Card from './Card';
import './CardList.css';

export default function CardList() {
    const cardListRef = useRef(null);

    // Duplicate data for smooth looping effect
    const repeatedCardData = [...cardData, ...cardData];

    useEffect(() => {
        const cardList = cardListRef.current;

        const handleScroll = () => {
            // Check if we've scrolled past the halfway point
            if (cardList.scrollLeft >= cardList.scrollWidth / 2) {
                // Reset scroll to the start of the duplicated list
                cardList.scrollLeft = 0;
            }
        };

        cardList.addEventListener('scroll', handleScroll);

        return () => {
            cardList.removeEventListener('scroll', handleScroll);
        };
    }, []);

    return (
        <div className="card-list" ref={cardListRef}>
            {repeatedCardData.map((item, index) => (
                <Card 
                    key={index}
                    imageUrl={item.image_url}
                    rating={item.rating}
                    reviews={item.reviews}
                    time={item.time}
                    title={item.title}
                    price={item.price}
                />
            ))}
        </div>
    );
}
