import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './CardDetail.css';

export default function CardDetail() {
    const { id } = useParams();
    const [card, setCard] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    useEffect(() => {
        // Fetch the card details and reviews from the backend API
        fetch(`http://localhost:5000/api/cards/${id}`)
            .then(response => response.json())
            .then(data => {
                setCard(data.card);
                setReviews(data.reviews);
            })
            .catch(error => console.error('Error fetching card details:', error));
    }, [id]);

    if (!card) return <p>Loading...</p>;

    // Array of image URLs for cycling
    const images = [card.image_url1, card.image_url2, card.image_url3, card.image_url4];

    // Handle next and previous image cycling
    const handleNextImage = () => {
        setCurrentImageIndex((currentImageIndex + 1) % images.length);
    };

    const handlePrevImage = () => {
        setCurrentImageIndex((currentImageIndex - 1 + images.length) % images.length);
    };

    return (
        <div className="card-detail-container">
            <div className="image-carousel">
                <button onClick={handlePrevImage}>❮</button>
                <img src={images[currentImageIndex]} alt={card.title} className="card-detail-image" />
                <button onClick={handleNextImage}>❯</button>
            </div>
            <div className="card-detail-content">
                <h2>{card.title}</h2>
                <p className="card-detail-rating">Rating: ⭐ {card.rating} ({card.review_count} reviews)</p>
                <p className="card-detail-time">Duration: {card.time}</p>
                <p className="card-detail-price">Price: {card.price}</p>
                <p className="card-detail-description">Here you can add more details or description about the experience.</p>
            </div>

            {/* Reviews Section */}
            <div className="reviews-section">
                <h3>Reviews:</h3>
                {reviews.length > 0 ? (
                    reviews.map((review, index) => (
                        <div key={index} className="review">
                            <p className="review-rating">⭐ {review.rating}</p>
                            <p className="review-text">{review.review_text}</p>
                            <p className="review-date">Date: {new Date(review.created_at).toLocaleDateString()}</p>
                        </div>
                    ))
                ) : (
                    <p>No reviews available for this experience.</p>
                )}
            </div>
        </div>
    );
}
