import React, { useRef, useEffect } from 'react';
import { FixedSizeList as List } from 'react-window';
import cardData from '../data/card_data.json';
import Card from './Card';
import './CardList.css';

export default function CardList() {
    const cardListRef = useRef();
    
    // Duplicate data for looping effect
    const LOOP_THRESHOLD = cardData.length * 2; // Length of the visible list for looping
    const repeatedCardData = [...cardData, ...cardData]; // Duplicate the data for seamless scrolling

    const handleScroll = ({ scrollOffset }) => {
        // Calculate max scroll based on item width and threshold
        const maxScroll = LOOP_THRESHOLD * 250; // 250px is the width of each item

        if (scrollOffset >= maxScroll) {
            // Reset scroll position when reaching the end of the duplicated list
            cardListRef.current.scrollTo(0);
        }
    };

    return (
        <List
            height={300} // Height of the container
            itemCount={LOOP_THRESHOLD} // Total number of items (double the original list for looping)
            itemSize={250} // Width of each item (adjust to match card width)
            layout="horizontal"
            width={800} // Width of the container (adjust as needed)
            ref={cardListRef}
            onScroll={handleScroll}
        >
            {({ index, style }) => {
                const item = repeatedCardData[index % cardData.length];
                return (
                    <div style={style}>
                        <Card
                            imageUrl={item.image_url}
                            rating={item.rating}
                            reviews={item.reviews}
                            time={item.time}
                            title={item.title}
                            price={item.price}
                        />
                    </div>
                );
            }}
        </List>
    );
}
