import React, { useRef } from 'react';
import { FixedSizeList as List } from 'react-window';
import cardData from '../data/card_data.json';
import Card from './Card';

const LOOP_THRESHOLD = cardData.length * 2; // Set a threshold to reset the scroll

export default function CardList() {
    const listRef = useRef();

    const handleScroll = ({ scrollOffset }) => {
        const maxScroll = LOOP_THRESHOLD * 250; // Adjust based on item width
        if (scrollOffset >= maxScroll) {
            listRef.current.scrollTo(0);
        }
    };

    return (
        <List
            height={300} // Adjust height to fit cards
            itemCount={LOOP_THRESHOLD}
            itemSize={250} // Width of each card in px
            layout="horizontal"
            width={800} // Adjust width to fit the viewport
            ref={listRef}
            onScroll={handleScroll}
        >
            {({ index, style }) => {
                const item = cardData[index % cardData.length];
                return (
                    <div style={style}>
                        <Card
                            imageUrl={item.image_url}
                            rating={item.rating}
                            reviews={item.reviews}
                            time={item.time}
                            title={item.title}
                            price={item.price}
                        />
                    </div>
                );
            }}
        </List>
    );
}
