import React, { useEffect, useState } from 'react';
import Card from './Card';
import './GridCardList.css';

export default function GridCardList() {
    const [cards, setCards] = useState([]);

    useEffect(() => {
        // Fetch data from the backend API instead of the JSON file
        fetch('http://192.168.1.60:5000/api/cards')
            .then(response => response.json())
            .then(data => setCards(data))
            .catch(error => console.error('Error fetching card data:', error));
    }, []);

    return (
        <div className="grid-card-list-container">
            <div className="grid-card-list">
                {cards.map((item) => (
                    <Card 
                        key={item.id}
                        id={item.id}               // Pass the id for navigation
                        imageUrl={item.image_url1} // Use the main image or primary URL field
                        rating={item.rating}
                        reviews={item.review_count}
                        time={item.time}
                        title={item.title}
                        price={item.price}
                    />
                ))}
            </div>
        </div>
    );
}
