import cardData from '../data/card_data.json';
import Card from './Card';
import './CardList.css';
import { FaChevronLeft, FaChevronRight } from 'react-icons/fa';
import { useRef, useEffect, useState } from 'react';

export default function CardList() {
    const cardListRef = useRef(null);
    const [cardWidth, setCardWidth] = useState(0);

    useEffect(() => {
        // Set the card width once the component mounts
        if (cardListRef.current) {
            const firstCard = cardListRef.current.querySelector('.card-container');
            if (firstCard) {
                setCardWidth(firstCard.offsetWidth + 16); // add gap size if any
            }
        }
    }, []);

    const scrollLeft = () => {
        cardListRef.current.scrollBy({ left: -cardWidth, behavior: 'smooth' });
    };

    const scrollRight = () => {
        cardListRef.current.scrollBy({ left: cardWidth, behavior: 'smooth' });
    };

    return (
        <div className="card-list-container">
            <div className="scroll-buttons">
                <button className="scroll-button" onClick={scrollLeft}>
                    <FaChevronLeft />
                </button>
                <button className="scroll-button" onClick={scrollRight}>
                    <FaChevronRight />
                </button>
            </div>
            <div className="card-list" ref={cardListRef}>
                {cardData.map((item, index) => (
                    <Card 
                        key={index}
                        imageUrl={item.image_url}
                        rating={item.rating}
                        reviews={item.reviews}
                        time={item.time}
                        title={item.title}
                        price={item.price}
                    />
                ))}
            </div>
        </div>
    );
}
