import React, { useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import { FaPhone, FaEnvelope } from 'react-icons/fa';
import './CardDetail.css';

export default function CardDetail() {
    const { id } = useParams();
    const [card, setCard] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const [showPopup, setShowPopup] = useState(false);
    const [reviewText, setReviewText] = useState('');
    const [rating, setRating] = useState('');
    const [reviewerName, setReviewerName] = useState('');
    const [error, setError] = useState('');
    const [hasReviewed, setHasReviewed] = useState(false);

    // Check if the user has reviewed this card based on cookies
    const checkIfReviewed = useCallback(() => {
        const reviewedCookie = document.cookie.split('; ').find(row => row.startsWith(`reviewed_card_${id}`));
        if (reviewedCookie) {
            setHasReviewed(true);
        }
    }, [id]);

    useEffect(() => {
        fetch(`http://192.168.1.60:5000/api/cards/${id}`)
            .then(response => response.json())
            .then(data => {
                setCard(data.card);
                setReviews(data.reviews);
                checkIfReviewed();
            })
            .catch(error => console.error('Error fetching card details:', error));
    }, [id, checkIfReviewed]);

    // Add Review
    const handleAddReview = () => {
        if (!rating || !reviewText || !reviewerName) {
            setError('All fields are required');
            return;
        }
        const reviewData = {
            cardId: id,
            reviewerName,
            reviewText,
            rating,
        };

        fetch('http://192.168.1.60:5000/api/reviews', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(reviewData),
        })
            .then(response => response.json())
            .then(data => {
                if (data.error) {
                    setError(data.error);
                } else {
                    setReviews(prevReviews => [...prevReviews, data.review]);
                    setHasReviewed(true);
                    document.cookie = `reviewed_card_${id}_name=${reviewerName}; path=/`;
                    setShowPopup(false);
                    setError('');
                }
            })
            .catch(error => {
                console.error('Error adding review:', error);
                setError('Failed to add review');
            });
    };

    // Delete Review
    const handleDeleteReview = () => {
        fetch(`http://192.168.1.60:5000/api/reviews/${id}`, {
            method: 'DELETE',
        })
            .then(response => {
                if (response.ok) {
                    setReviews(prevReviews => prevReviews.filter(review => review.card_id !== id));
                    setHasReviewed(false);
                    document.cookie = `reviewed_card_${id}_name=; path=/; expires=Thu, 01 Jan 1970 00:00:00 UTC;`;
                } else {
                    setError('Failed to delete review');
                }
            })
            .catch(error => {
                console.error('Error deleting review:', error);
                setError('Failed to delete review');
            });
    };

    // Render
    if (!card) return <p>Loading...</p>;

    const images = [card.image_url1, card.image_url2, card.image_url3, card.image_url4];
    const nextImage = () => setCurrentImageIndex((currentImageIndex + 1) % images.length);
    const prevImage = () => setCurrentImageIndex((currentImageIndex - 1 + images.length) % images.length);

    return (
        <div className="card-detail-container">
            <div className="card-detail-main">
                <div className="card-detail-image-container">
                    <button className="carousel-button" onClick={prevImage}>&lt;</button>
                    <img src={images[currentImageIndex]} alt={`Slide ${currentImageIndex + 1}`} className="carousel-image" />
                    <button className="carousel-button" onClick={nextImage}>&gt;</button>
                    <h2 className="hosted-by">{card.hosted_by}</h2>
                    <p className="price">₹{card.price}</p>
                </div>

                <div className="card-detail-content">
                    <div className="details-box">
                        <h2>{card.title}</h2>
                        <p><strong>Duration:</strong> {card.time}</p>
                        <p><strong>Experience Date:</strong> {new Date(card.experience_date).toLocaleDateString()}</p>
                        <p><strong>Average Rating:</strong> {card.rating || 0.0} ({card.review_count || 0} reviews)</p>
                    </div>

                    <div className="description-box">
                        <h3>Description</h3>
                        <p>{card.description}</p>
                    </div>

                    <div className="contact-host-container">
                        <h3>Contact Host</h3>
                        <div className="contact-info">
                            <a href={`tel:${card.telephone}`} className="contact-button">
                                <FaPhone className="contact-icon" /> {card.telephone}
                            </a>
                            <a href={`mailto:${card.email}`} className="contact-button">
                                <FaEnvelope className="contact-icon" /> {card.email}
                            </a>
                        </div>
                    </div>
                </div>
            </div>

            <div className="reviews-container">
                <div className="reviews-header">
                    <h3>Reviews:</h3>
                    {hasReviewed ? (
                        <button className="delete-review-button" onClick={handleDeleteReview}>
                            Delete My Review
                        </button>
                    ) : (
                        <button className="add-review-button" onClick={() => setShowPopup(true)}>
                            Add Review
                        </button>
                    )}
                </div>

                {reviews.map((review, index) => (
                    <div key={index} className="review">
                        <p><strong>Reviewer:</strong> {review.reviewer_name || "Anonymous"}</p>
                        <p><strong>Rating:</strong> {review.rating} ⭐</p>
                        <p>{review.review_text}</p>
                        <p><em>Reviewed on: {new Date(review.created_at).toLocaleDateString()}</em></p>
                    </div>
                ))}
            </div>

            {showPopup && (
                <div className="popup">
                    <h3>Add Review</h3>
                    <input
                        type="text"
                        placeholder="Your Name"
                        value={reviewerName}
                        onChange={e => setReviewerName(e.target.value)}
                    />
                    <input
                        type="number"
                        placeholder="Rating (1-5)"
                        value={rating}
                        onChange={e => setRating(e.target.value)}
                        min="1"
                        max="5"
                    />
                    <textarea
                        placeholder="Write your review"
                        value={reviewText}
                        onChange={e => setReviewText(e.target.value)}
                    ></textarea>
                    {error && <p className="error">{error}</p>}
                    <button onClick={handleAddReview}>Submit</button>
                    <button onClick={() => setShowPopup(false)}>Cancel</button>
                </div>
            )}
        </div>
    );
}
