import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { FaPhone, FaEnvelope } from 'react-icons/fa';
import './CardDetail.css';

export default function CardDetail() {
    const { id } = useParams();
    const [card, setCard] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [showReviewForm, setShowReviewForm] = useState(false);
    const [reviewerName, setReviewerName] = useState('');
    const [rating, setRating] = useState('');
    const [reviewText, setReviewText] = useState('');
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    useEffect(() => {
        fetch(`http://192.168.1.60:5000/api/cards/${id}`)
            .then(response => response.json())
            .then(data => {
                setCard(data.card);
                setReviews(data.reviews);
            })
            .catch(error => console.error('Error fetching card details:', error));
    }, [id]);

    const handleReviewSubmit = (e) => {
        e.preventDefault();

        const reviewData = { cardId: id, reviewerName, rating, reviewText };

        fetch('http://192.168.1.60:5000/api/reviews', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(reviewData)
        })
        .then(response => response.json())
        .then(data => {
            if (data.review) {
                setReviews([data.review, ...reviews]);
                setReviewerName('');
                setRating('');
                setReviewText('');
                setShowReviewForm(false);
            }
        })
        .catch(error => console.error('Error submitting review:', error));
    };

    const images = card ? [card.image_url1, card.image_url2, card.image_url3, card.image_url4] : [];

    if (!card) return <p>Loading...</p>;

    return (
        <div className="card-detail-container">
            <div className="card-detail-main">
                <div className="card-detail-image-container">
                    <button className="carousel-button" onClick={() => setCurrentImageIndex((currentImageIndex - 1 + images.length) % images.length)}>&lt;</button>
                    <img src={images[currentImageIndex]} alt={`Slide ${currentImageIndex + 1}`} className="carousel-image" />
                    <button className="carousel-button" onClick={() => setCurrentImageIndex((currentImageIndex + 1) % images.length)}>&gt;</button>
                    <h2 className="hosted-by">{card.hosted_by}</h2>
                    <p className="price">₹{card.price}</p>
                </div>

                <div className="card-detail-content">
                    <div className="details-box">
                        <h2>{card.title}</h2>
                        <p><strong>Duration:</strong> {card.time}</p>
                        <p><strong>Experience Date:</strong> {new Date(card.experience_date).toLocaleDateString()}</p>
                        <p><strong>Average Rating:</strong> {card.rating || 0.0} ({card.review_count || 0} reviews)</p>
                    </div>

                    <div className="description-box">
                        <h3>Description</h3>
                        <p>{card.description}</p>
                    </div>

                    <div className="contact-host-container">
                        <h3>Contact Host</h3>
                        <div className="contact-info">
                            <a href={`tel:${card.telephone}`} className="contact-button"><FaPhone className="contact-icon" /> {card.telephone}</a>
                            <a href={`mailto:${card.email}`} className="contact-button"><FaEnvelope className="contact-icon" /> {card.email}</a>
                        </div>
                    </div>
                </div>
            </div>

            <div className="reviews-container">
                <h3>Reviews:</h3>
                <button onClick={() => setShowReviewForm(true)} className="add-review-button">Add Review</button>
                {showReviewForm && (
                    <div className="review-form-overlay">
                        <form onSubmit={handleReviewSubmit} className="review-form">
                            <h4>Add Your Review</h4>
                            <input type="text" placeholder="Your Name" value={reviewerName} onChange={(e) => setReviewerName(e.target.value)} required />
                            <input type="number" placeholder="Rating (1-5)" value={rating} onChange={(e) => setRating(e.target.value)} min="1" max="5" required />
                            <textarea placeholder="Write your review here" value={reviewText} onChange={(e) => setReviewText(e.target.value)} required />
                            <button type="submit">Submit Review</button>
                            <button type="button" onClick={() => setShowReviewForm(false)}>Cancel</button>
                        </form>
                    </div>
                )}
                {reviews.map((review, index) => (
                    <div key={index} className="review">
                        <p><strong>Reviewer:</strong> {review.reviewer_name || "Anonymous"}</p>
                        <p><strong>Rating:</strong> {review.rating} ⭐</p>
                        <p>{review.review_text}</p>
                        <p><em>Reviewed on: {new Date(review.created_at).toLocaleDateString()}</em></p>
                    </div>
                ))}
            </div>
        </div>
    );
}
